// src/pages/AdminLogin.jsx
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function AdminLogin() {
  const navigate = useNavigate();
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 1500);
    return () => clearTimeout(timer);
  }, []);

  const handleLogin = () => {
    const ADMIN_PASSWORD = process.env.REACT_APP_ADMIN_PASSWORD;

    if (password === ADMIN_PASSWORD) {
      navigate("/admin-dashboard");
    } else {
      setError("Incorrect password. Please try again.");
    }
  };

  if (loading) {
    return (
      <div className="h-screen flex items-center justify-center bg-black text-white">
        <h1 className="text-4xl font-bold animate-pulse text-purple-500">Loading Admin Page...</h1>
      </div>
    );
  }

  return (
    <div
      className="min-h-screen bg-cover bg-center flex flex-col items-center justify-center text-white px-4"
      style={{
        backgroundImage:
          "url('https://eetgxwgbysyezclsecwd.supabase.co/storage/v1/object/public/public-assets//wallpaperflare.com_wallpaper%20(57).jpg')",
      }}
    >
      <div className="bg-gray-900 bg-opacity-80 p-6 max-w-md w-full rounded-xl shadow-xl">
        <img
          src="https://eetgxwgbysyezclsecwd.supabase.co/storage/v1/object/public/public-assets//Asset%202%20(2).png"
          alt="Obscura Logo"
          className="mx-auto max-w-xs mb-4"
        />
        <h1 className="text-2xl font-bold mb-4 text-purple-400 text-center">Admin Login</h1>
        <input
          type="password"
          placeholder="Enter admin password"
          className="w-full p-3 rounded bg-gray-800 border border-gray-700 mb-2"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {error && <p className="text-red-500 mb-2 text-center">{error}</p>}
        <button
          onClick={handleLogin}
          className="w-full bg-purple-600 hover:bg-purple-700 px-6 py-3 rounded text-white font-semibold"
        >
          Login
        </button>
      </div>
    </div>
  );
}
