// Full Admin Dashboard without PDF export + Applicants and Minor Applicants Display + Bulk Edit + Modal Style Fix
import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";
import { CSVLink } from "react-csv";
import Papa from "papaparse";

export default function AdminDashboard() {
  const [view, setView] = useState("overview");
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [modalData, setModalData] = useState(null);
  const [selectedRows, setSelectedRows] = useState([]);
  const [counts, setCounts] = useState({ total: 0, approved: 0, declined: 0, full: 0 });

  const fetchData = async (table) => {
    setLoading(true);
    const { data, error } = await supabase.from(table).select("*").order("created_at", { ascending: true });
    if (error) console.error(`Error fetching ${table}:`, error);
    setData(data || []);
    setFilteredData(data || []);
    setLoading(false);
  };

  const handleImportCSV = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: async (results) => {
        const parsedData = results.data;

        const cleaned = parsedData.map((row) => ({
          ...row,
          camera: row.camera === "true" || row.camera === "Yes",
          sponsors: row.sponsors === "true" || row.sponsors === "Yes",
          isCreator: row.isCreator === "true" || row.isCreator === "Yes",
          ndaAgreement: row.ndaAgreement === "true" || row.ndaAgreement === "Yes",
          yearsCreating: row.yearsCreating ? parseInt(row.yearsCreating) : null
        }));

        const { error } = await supabase.from(view).insert(cleaned);
        if (error) {
          alert("Import failed: " + error.message);
        } else {
          alert("CSV imported successfully!");
          fetchData(view);
        }
      },
      error: (err) => {
        console.error("CSV parse error:", err);
        alert("Failed to parse CSV: " + err.message);
      }
    });
  };

  const updateStatus = async (id, newStatus, notes = "") => {
    const { error } = await supabase.from(view).update({ status: newStatus, decline_notes: notes }).eq("id", id);
    if (error) alert("Status update failed: " + error.message);
    else fetchData(view);
  };

  const updateField = async (id, field, value) => {
    if (field === "fullName" || field === "dob") return;
    const { error } = await supabase.from(view).update({ [field]: value }).eq("id", id);
    if (error) alert(`Update failed: ${error.message}`);
    else fetchData(view);
  };

  const handleBulkStatus = async (status) => {
    if (selectedRows.length === 0) return alert("No rows selected");
    const { error } = await supabase.from(view)
      .update({ status })
      .in("id", selectedRows);
    if (error) alert("Bulk update failed: " + error.message);
    else {
      alert("Bulk update successful");
      setSelectedRows([]);
      fetchData(view);
    }
  };

  useEffect(() => {
    if (view === "overview") return;
    fetchData(view);
  }, [view]);

  useEffect(() => {
    if (search.trim() === "") setFilteredData(data);
    else setFilteredData(data.filter(row => Object.values(row).join(" ").toLowerCase().includes(search.toLowerCase())));
  }, [search, data]);

  return (
    <div className="min-h-screen p-6 text-white bg-cover bg-center" style={{ backgroundImage: "url('https://eetgxwgbysyezclsecwd.supabase.co/storage/v1/object/public/public-assets//wallpaperflare.com_wallpaper%20(57).jpg')" }}>
      <div className="bg-gray-900 bg-opacity-90 p-6 rounded-xl shadow-lg max-w-7xl mx-auto">
        <div className="flex justify-center gap-4 mb-4">
          <button onClick={() => setView("overview")} className={`px-4 py-2 rounded ${view === "overview" ? "bg-purple-600" : "bg-gray-700"}`}>Overview</button>
          <button onClick={() => setView("applicants")} className={`px-4 py-2 rounded ${view === "applicants" ? "bg-purple-600" : "bg-gray-700"}`}>Applicants</button>
          <button onClick={() => setView("minor_applicants")} className={`px-4 py-2 rounded ${view === "minor_applicants" ? "bg-purple-600" : "bg-gray-700"}`}>Minors</button>
        </div>

        <div className="flex justify-between mb-3">
          <input type="text" value={search} onChange={(e) => setSearch(e.target.value)} placeholder="Search..." className="p-2 rounded bg-gray-800 border border-gray-600 w-1/3" />
          <div className="flex gap-2">
            <CSVLink data={data} filename={`${view}.csv`} className="bg-blue-500 px-4 py-2 rounded text-white">Export CSV</CSVLink>
            <label className="bg-green-600 px-4 py-2 rounded text-white cursor-pointer">
              Import CSV
              <input type="file" accept=".csv" onChange={handleImportCSV} className="hidden" />
            </label>
          </div>
        </div>

        {view !== "overview" && (
          <div className="flex gap-4 mb-4">
            <button onClick={() => handleBulkStatus("approved")} className="bg-green-600 px-4 py-2 rounded">Bulk Approve</button>
            <button onClick={() => handleBulkStatus("declined")} className="bg-red-600 px-4 py-2 rounded">Bulk Decline</button>
          </div>
        )}

        <div className="overflow-x-auto">
          <table className="w-full text-sm border border-gray-600 mt-2">
            <thead>
              <tr className="bg-purple-800 text-white">
                <th className="p-2 border">
                  <input
                    type="checkbox"
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedRows(filteredData.map(r => r.id));
                      } else {
                        setSelectedRows([]);
                      }
                    }}
                    checked={selectedRows.length === filteredData.length && filteredData.length > 0}
                  />
                </th>
                {(view === "applicants") ? (
                  <>
                    <th className="p-2 border">Full Name</th>
                    <th className="p-2 border">Email</th>
                    <th className="p-2 border">Phone</th>
                    <th className="p-2 border">Division</th>
                    <th className="p-2 border">Status</th>
                    <th className="p-2 border">Photo</th>
                    <th className="p-2 border">Created</th>
                    <th className="p-2 border">Actions</th>
                  </>
                ) : (
                  <>
                    <th className="p-2 border">Full Name</th>
                    <th className="p-2 border">Guardian Name</th>
                    <th className="p-2 border">Guardian Phone</th>
                    <th className="p-2 border">DOB</th>
                    <th className="p-2 border">Status</th>
                    <th className="p-2 border">Notes</th>
                    <th className="p-2 border">Created</th>
                    <th className="p-2 border">Actions</th>
                  </>
                )}
              </tr>
            </thead>
            <tbody>
              {filteredData.map((row) => (
                <tr key={row.id} className="hover:bg-gray-800">
                  <td className="p-2 border text-center">
                    <input
                      type="checkbox"
                      checked={selectedRows.includes(row.id)}
                      onChange={() => {
                        setSelectedRows((prev) =>
                          prev.includes(row.id)
                            ? prev.filter(id => id !== row.id)
                            : [...prev, row.id]
                        );
                      }}
                    />
                  </td>
                  {(view === "applicants") ? (
                    <>
                      <td className="p-2 border">{row.fullName}</td>
                      <td className="p-2 border">{row.email}</td>
                      <td className="p-2 border">{row.phone}</td>
                      <td className="p-2 border">{row.division}</td>
                      <td className="p-2 border capitalize">{row.status}</td>
                      <td className="p-2 border text-center">
                        {row.photo_url ? (
                          <a href={row.photo_url} target="_blank" rel="noopener noreferrer" className="text-blue-400 underline">View</a>
                        ) : "N/A"}
                      </td>
                      <td className="p-2 border">{new Date(row.created_at).toLocaleDateString()}</td>
                      <td className="p-2 border space-y-1">
                        <button onClick={() => updateStatus(row.id, "approved")} className="bg-green-600 px-2 py-1 rounded mr-1">Approve</button>
                        <button onClick={() => { const notes = prompt("Enter decline reason:"); if (notes) updateStatus(row.id, "declined", notes); }} className="bg-red-600 px-2 py-1 rounded mr-1">Decline</button>
                        <button onClick={() => updateStatus(row.id, "banned")} className="bg-black px-2 py-1 rounded mr-1">Ban</button>
                        <button onClick={() => setModalData(row)} className="bg-blue-500 px-2 py-1 rounded">Edit</button>
                      </td>
                    </>
                  ) : (
                    <>
                      <td className="p-2 border">{row.fullName}</td>
                      <td className="p-2 border">{row.guardianName}</td>
                      <td className="p-2 border">{row.guardianPhone}</td>
                      <td className="p-2 border">{row.dob}</td>
                      <td className="p-2 border capitalize">{row.status}</td>
                      <td className="p-2 border">{row.decline_notes || "-"}</td>
                      <td className="p-2 border">{new Date(row.created_at).toLocaleDateString()}</td>
                      <td className="p-2 border space-y-1">
                        <button onClick={() => updateStatus(row.id, "approved")} className="bg-green-600 px-2 py-1 rounded mr-1">Approve</button>
                        <button onClick={() => { const notes = prompt("Enter decline reason:"); if (notes) updateStatus(row.id, "declined", notes); }} className="bg-red-600 px-2 py-1 rounded mr-1">Decline</button>
                        <button onClick={() => updateStatus(row.id, "banned")} className="bg-black px-2 py-1 rounded mr-1">Ban</button>
                        <button onClick={() => setModalData(row)} className="bg-blue-500 px-2 py-1 rounded">Edit</button>
                      </td>
                    </>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {modalData && (
          <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50">
            <div className="bg-gray-900 text-white p-6 rounded-xl shadow-xl max-w-lg w-full max-h-[90vh] overflow-y-auto">
              <h2 className="text-lg font-semibold mb-4">Edit Entry</h2>
              {Object.entries(modalData).map(([key, value]) => {
                if (["id", "fullName", "dob"].includes(key)) return null;
                const handleFieldChange = (e) => {
                  let newValue = e.target.value;
                  if (["platforms", "languages", "software"].includes(key)) newValue = newValue.split(",").map(s => s.trim());
                  if (["camera", "sponsors", "ndaAgreement", "isCreator"].includes(key)) newValue = e.target.value === "true";
                  setModalData({ ...modalData, [key]: newValue });
                };
                return (
                  <div key={key} className="mb-3">
                    <label className="block text-sm font-semibold mb-1 capitalize">{key}</label>
                    {["camera", "sponsors", "isCreator", "ndaAgreement"].includes(key) ? (
                      <select className="w-full p-2 border rounded" value={String(value)} onChange={handleFieldChange}>
                        <option value="true">Yes</option>
                        <option value="false">No</option>
                      </select>
                    ) : (
                      <input className="w-full p-2 border rounded" type="text" value={value || ""} onChange={handleFieldChange} />
                    )}
                  </div>
                );
              })}
              <div className="flex justify-end gap-2 mt-4">
                <button onClick={() => setModalData(null)} className="bg-gray-500 px-4 py-2 rounded text-white">Cancel</button>
                <button onClick={() => {
                  Object.entries(modalData).forEach(([k, v]) => {
                    if (!["id", "fullName", "dob"].includes(k)) updateField(modalData.id, k, v);
                  });
                  setModalData(null);
                }} className="bg-purple-600 px-4 py-2 rounded text-white">Save</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
