import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";

export default function AdminUserManager() {
  const [users, setUsers] = useState([]);
  const [role, setRole] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Check if current user is superadmin
  useEffect(() => {
    const checkAuth = async () => {
      const {
        data: { user },
      } = await supabase.auth.getUser();

      if (!user) return setError("Not logged in.");

      const { data, error } = await supabase
        .from("users")
        .select("role")
        .eq("id", user.id)
        .single();

      if (error || data?.role !== "superadmin") {
        return setError("Access denied. Superadmin only.");
      }

      setRole(data.role);
    };

    checkAuth();
  }, []);

  // Fetch all users
  useEffect(() => {
    if (role === "superadmin") {
      const fetchUsers = async () => {
        const { data, error } = await supabase
          .from("users")
          .select("id, email, role, created_at")
          .order("created_at", { ascending: true });

        if (error) {
          setError("Failed to fetch users");
        } else {
          setUsers(data);
        }

        setLoading(false);
      };

      fetchUsers();
    }
  }, [role]);

  // Promote to admin
  const promoteToAdmin = async (id) => {
    const { error } = await supabase
      .from("users")
      .update({ role: "admin" })
      .eq("id", id);

    if (!error) {
      setUsers((prev) =>
        prev.map((u) => (u.id === id ? { ...u, role: "admin" } : u))
      );
    } else {
      alert("Failed to promote: " + error.message);
    }
  };

  if (error) {
    return <div className="p-10 text-center text-red-500 font-bold">{error}</div>;
  }

  if (loading) {
    return <div className="p-10 text-center">Loading users...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4 text-center">User Management</h1>
      <table className="w-full border-collapse rounded shadow overflow-hidden">
        <thead className="bg-gray-100 text-left">
          <tr>
            <th className="p-3">Email</th>
            <th className="p-3">Role</th>
            <th className="p-3">Joined</th>
            <th className="p-3 text-center">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id} className="border-t">
              <td className="p-3">{user.email}</td>
              <td className="p-3 capitalize">{user.role}</td>
              <td className="p-3 text-sm text-gray-500">
                {new Date(user.created_at).toLocaleDateString()}
              </td>
              <td className="p-3 text-center">
                {user.role === "user" ? (
                  <button
                    onClick={() => promoteToAdmin(user.id)}
                    className="bg-purple-600 hover:bg-purple-700 text-white px-3 py-1 rounded text-sm"
                  >
                    Promote to Admin
                  </button>
                ) : (
                  <span className="text-gray-400 text-sm">No actions</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
