import { useState, useEffect } from "react";
import { supabase } from "../supabaseClient";
import CreatorFieldsCard from "./CreatorFieldsCard";
import { useNavigate } from "react-router-dom";

const Button = (props) => (
  <button
    {...props}
    className={
      "bg-purple-600 hover:bg-purple-700 px-6 py-2 rounded text-white font-semibold " +
      (props.className || "")
    }
  >
    {props.children}
  </button>
);

export default function RecruitmentForm() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    gamertag: "",
    discord: "",
    gender: "",
    dob: "",
    division: "",
    photo: null,
    competitive: "yes",
    experience: "",
    isCreator: false,
    ndaAgreement: false,
    creatorName: "",
    timezone: "",
    platforms: [],
    otherPlatform: "",
    schedule: "",
    contentType: "",
    games: "",
    languages: [],
    internet: "",
    software: [],
    equipment: "",
    yearsCreating: "",
    sponsorList: "",
    camera: false,
    sponsors: false,
    collabs: "no",
    creatorGoals: "",
    creatorNotes: ""
  });

  const [loading, setLoading] = useState(true);
  const [under18, setUnder18] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 1500);
    return () => clearTimeout(timer);
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    let updatedValue = type === "checkbox" ? checked : type === "file" ? files[0] : value;

    if (name === "isCreator") {
      updatedValue = value === "yes";
    }

    setFormData((prev) => ({
      ...prev,
      [name]: updatedValue,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const age = new Date().getFullYear() - new Date(formData.dob).getFullYear();

    if (!formData.fullName || !formData.email || !formData.phone) {
      alert("Full Name, Email, and Phone are required.");
      return;
    }

    if (!formData.ndaAgreement) {
      alert("You must complete the NDA before submitting.");
      return;
    }

    if (age < 18) {
      setUnder18(true);
      return;
    }

    let photoUrl = "";
    if (formData.photo) {
      const fileExt = formData.photo.name.split(".").pop();
      const fileName = `${formData.fullName.replace(/\s+/g, "_")}_${Date.now()}.${fileExt}`;
      const { error: uploadError } = await supabase.storage.from("photos").upload(fileName, formData.photo);
      if (uploadError) {
        alert("Failed to upload photo: " + uploadError.message);
        return;
      }
      const { data: publicUrlData } = supabase.storage.from("photos").getPublicUrl(fileName);
      photoUrl = publicUrlData?.publicUrl || "";
    }

    const { error: insertError } = await supabase.from("applicants").insert([{ ...formData, photo_url: photoUrl }]);
    if (insertError) {
      alert("Submission failed: " + insertError.message);
    } else {
      alert("Application submitted successfully!");
      window.location.reload();
    }
  };

  if (loading) {
    return (
      <div className="h-screen flex items-center justify-center bg-black text-white">
        <h1 className="text-4xl font-bold animate-pulse text-purple-500">Loading Obscura Recruitment...</h1>
      </div>
    );
  }

 if (under18) {
  return (
    <div className="min-h-screen flex flex-col justify-center items-center bg-gray-950 text-white px-4 py-10 text-center">
      <h2 className="text-2xl md:text-4xl font-bold text-red-500 mb-4">Parental Consent Required</h2>
      <p className="text-md md:text-lg max-w-2xl mb-6">
        You must be 18 years or older to complete this recruitment form. If you are under 18,
        please have a parent or legal guardian fill out the Minor Consent Form below.
      </p>
      <div className="flex flex-col md:flex-row items-center gap-4">
        <a
          href="https://na4.documents.adobe.com/public/esignWidget?wid=CBFCIBAA3AAABLblqZhA8VeSehpag-n1HVFX5gdMupLI5UIGYiNGXNWXkqThp2G--ZFZ3hZWaUKbgBQSQsws*"
          target="_blank"
          rel="noopener noreferrer"
          className="bg-purple-600 hover:bg-purple-700 px-6 py-3 text-white font-semibold rounded-lg shadow-lg"
        >
          Sign the Minor Consent Form
        </a>
        <button
          onClick={() => navigate("/minor-recruitment")}
          className="bg-green-600 hover:bg-green-700 px-6 py-3 text-white font-semibold rounded-lg shadow-lg"
        >
          Proceed to Minor Recruitment Form
        </button>
      </div>
    </div>
  );
}

  return (
    <div className="bg-cover bg-center min-h-screen text-white p-6 flex flex-col items-center" style={{ backgroundImage: "url('https://eetgxwgbysyezclsecwd.supabase.co/storage/v1/object/public/public-assets//wallpaperflare.com_wallpaper%20(57).jpg')" }}>
      <div className="bg-gray-900 bg-opacity-80 p-6 max-w-4xl w-full rounded-xl shadow-xl">
        <img
          src="https://eetgxwgbysyezclsecwd.supabase.co/storage/v1/object/public/public-assets//Asset%202%20(2).png"
          alt="Obscura Logo"
          className="mx-auto max-w-xs mb-4"
        />
        <h1 className="text-3xl font-bold text-purple-400 mb-6">Obscura E-Sports Recruitment Form</h1>
        <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <h2 className="text-lg font-semibold text-purple-300 mb-2">Personal Details</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input name="fullName" placeholder="Full Name" value={formData.fullName} onChange={handleChange} required className="p-3 rounded bg-gray-800 border border-gray-700" />
            <input name="email" type="email" placeholder="Email" value={formData.email} onChange={handleChange} required className="p-3 rounded bg-gray-800 border border-gray-700" />
            <input name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} required className="p-3 rounded bg-gray-800 border border-gray-700" />
            <input name="gamertag" placeholder="Gamertag" value={formData.gamertag} onChange={handleChange} className="p-3 rounded bg-gray-800 border border-gray-700" />
            <input name="discord" placeholder="Discord" value={formData.discord} onChange={handleChange} className="p-3 rounded bg-gray-800 border border-gray-700" />
            <select name="gender" value={formData.gender} onChange={handleChange} className="p-3 rounded bg-gray-800 border border-gray-700">
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
            <input name="dob" type="date" value={formData.dob} onChange={handleChange} className="p-3 rounded bg-gray-800 border border-gray-700" />
            <input name="division" placeholder="Division" value={formData.division} onChange={handleChange} className="p-3 rounded bg-gray-800 border border-gray-700" />
          </div>
        </div>

        <div>
          <label className="block mb-1 text-purple-300">Upload a Photo of Yourself</label>
          <input name="photo" type="file" accept="image/*" onChange={handleChange} className="w-full text-white p-2 bg-gray-800 rounded border border-gray-600" />
        </div>

        <div>
          <h2 className="text-lg font-semibold text-purple-300 mb-2">Competitive Experience</h2>
          <select name="competitive" value={formData.competitive} onChange={handleChange} className="p-3 rounded bg-gray-800 border border-gray-700">
            <option value="yes">Yes</option>
            <option value="no">No</option>
            <option value="maybe">Not Sure</option>
          </select>
          {(formData.competitive === "yes" || formData.competitive === "maybe") && (
            <input name="experience" placeholder="Competitive Experience" value={formData.experience} onChange={handleChange} className="w-full mt-2 p-3 rounded bg-gray-800 border border-gray-700" />
          )}
        </div>

        <div>
          <h2 className="text-lg font-semibold text-purple-300 mb-2">Are you a Content Creator?</h2>
          <select name="isCreator" value={formData.isCreator ? "yes" : "no"} onChange={handleChange} className="p-3 rounded bg-gray-800 border border-gray-700">
            <option value="no">No</option>
            <option value="yes">Yes</option>
          </select>
        </div>

        {formData.isCreator && <CreatorFieldsCard formData={formData} handleChange={handleChange} />}

        <div>
          <h2 className="text-xl font-semibold text-orange-400">Non-Disclosure Agreement</h2>
          <iframe
            src="https://na4.documents.adobe.com/public/esignWidget?wid=CBFCIBAA3AAABLblqZhB4n42WYd9IJQDzDGl1igd07ph8f1448tjVOqwrgoBIXdfaY3rrPeushEben3hBD4M*&hosted=false"
            width="100%"
            height="600px"
            frameBorder="0"
            className="border border-gray-700 rounded-lg"
          ></iframe>
          <label className="flex items-center space-x-2 mt-2">
            <input type="checkbox" name="ndaAgreement" checked={formData.ndaAgreement} onChange={handleChange} className="text-orange-500" required />
            <span>I agree to the NDA</span>
          </label>
        </div>

        <div className="text-center">
          <Button type="submit">Submit</Button>
        </div>
      </form>
      </div>
    </div>
  );
}
