import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";
import { CSVLink } from "react-csv";
import jsPDF from "jspdf";
import "jspdf-autotable";

export default function AdminDashboard() {
  const [view, setView] = useState("overview");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [counts, setCounts] = useState({ total: 0, approved: 0, declined: 0, full: 0 });

  const fetchData = async (table) => {
    setLoading(true);
    const { data, error } = await supabase
      .from(table)
      .select("*")
      .order("created_at", { ascending: true });
    if (error) console.error(`Error fetching ${table}:`, error);
    setData(data || []);
    setLoading(false);
  };

  const fetchOverviewStats = async () => {
    const { count: pending } = await supabase
      .from("applicants")
      .select("id", { count: "exact" })
      .eq("status", "pending");
    const { count: approved } = await supabase
      .from("applicants")
      .select("id", { count: "exact" })
      .eq("status", "approved");
    const { count: declined } = await supabase
      .from("applicants")
      .select("id", { count: "exact" })
      .eq("status", "declined");

    const full = (pending || 0) + (approved || 0) + (declined || 0);
    setCounts({ total: pending || 0, approved: approved || 0, declined: declined || 0, full });
  };

  const updateStatus = async (id, newStatus, notes = "") => {
    const { error } = await supabase
      .from(view)
      .update({ status: newStatus, decline_notes: notes })
      .eq("id", id);

    if (error) {
      console.error("Status update failed:", error.message);
      alert("Status update failed: " + error.message);
    } else {
      fetchData(view);
      fetchOverviewStats();
    }
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    const tableColumn = Object.keys(data[0] || {});
    const tableRows = data.map(row => tableColumn.map(col => row[col]));

    doc.text(`${view.toUpperCase()} Export`, 14, 16);
    doc.autoTable({ head: [tableColumn], body: tableRows, startY: 20 });
    doc.save(`${view}.pdf`);
  };

  useEffect(() => {
    if (view === "overview") {
      fetchOverviewStats();
    } else {
      fetchData(view);
    }
  }, [view]);

  const columns = view === "applicants"
    ? ["Full Name", "Email", "Phone", "Division", "Competitive", "Status", "Photo", "Actions"]
    : ["Full Name", "Guardian Name", "Phone", "DOB", "Status", "Actions"];

  return (
    <div className="min-h-screen p-6 text-white bg-cover bg-center" style={{ backgroundImage: "url('https://eetgxwgbysyezclsecwd.supabase.co/storage/v1/object/public/public-assets//wallpaperflare.com_wallpaper%20(57).jpg')" }}>
      <div className="bg-gray-900 bg-opacity-90 p-6 rounded-xl shadow-lg max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold mb-6 text-purple-400 text-center">Admin Dashboard</h1>

        <div className="flex justify-center gap-4 mb-6">
          <button onClick={() => setView("overview")} className={`px-4 py-2 rounded ${view === "overview" ? "bg-purple-600" : "bg-gray-700"}`}>Overview</button>
          <button onClick={() => setView("applicants")} className={`px-4 py-2 rounded ${view === "applicants" ? "bg-purple-600" : "bg-gray-700"}`}>Applicants</button>
          <button onClick={() => setView("minor_applicants")} className={`px-4 py-2 rounded ${view === "minor_applicants" ? "bg-purple-600" : "bg-gray-700"}`}>Minors</button>
        </div>

        {view === "overview" ? (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
            <div className="bg-purple-700 p-4 rounded-lg shadow text-white">
              <p className="text-xs font-semibold">Pending</p>
              <p className="text-lg font-bold">{counts.total}</p>
              <p className="text-xs">{`${counts.total}/${counts.full}`}</p>
            </div>
            <div className="bg-green-600 p-4 rounded-lg shadow text-white">
              <p className="text-xs font-semibold">Approved</p>
              <p className="text-lg font-bold">{counts.approved}</p>
            </div>
            <div className="bg-red-600 p-4 rounded-lg shadow text-white">
              <p className="text-xs font-semibold">Declined</p>
              <p className="text-lg font-bold">{counts.declined}</p>
            </div>
            <div className="bg-gray-600 p-4 rounded-lg shadow text-white">
              <p className="text-xs font-semibold">Total</p>
              <p className="text-lg font-bold">{counts.full}</p>
            </div>
          </div>
        ) : loading ? (
          <p className="text-center text-lg">Loading...</p>
        ) : (
          <>
            <div className="flex justify-end gap-2 mb-2">
              <CSVLink data={data} filename={`${view}.csv`} className="bg-blue-500 px-4 py-2 rounded text-white">
                Export CSV
              </CSVLink>
              <button onClick={exportPDF} className="bg-red-500 px-4 py-2 rounded text-white">
                Export PDF
              </button>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full text-sm border-collapse border border-gray-700 mt-4">
                <thead>
                  <tr className="bg-purple-700">
                    {columns.map((col) => (
                      <th key={col} className="border border-gray-600 p-2 whitespace-nowrap">{col}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {data.length === 0 ? (
                    <tr>
                      <td colSpan={columns.length} className="text-center p-4">No data available.</td>
                    </tr>
                  ) : (
                    data.map((row) => (
                      <tr key={row.id} className="hover:bg-gray-800">
                        <td className="border p-2 whitespace-nowrap">{row.fullName}</td>
                        {view === "applicants" ? (
                          <>
                            <td className="border p-2 whitespace-nowrap">{row.email}</td>
                            <td className="border p-2 whitespace-nowrap">{row.phone}</td>
                            <td className="border p-2 whitespace-nowrap">{row.division}</td>
                            <td className="border p-2 whitespace-nowrap">{row.competitive}</td>
                            <td className="border p-2 whitespace-nowrap capitalize">{row.status}</td>
                            <td className="border p-2 text-center">
                              {row.photo_url ? (
                                <a href={row.photo_url} target="_blank" rel="noopener noreferrer" className="text-blue-400 underline">
                                  View / Download
                                </a>
                              ) : (
                                "N/A"
                              )}
                            </td>
                          </>
                        ) : (
                          <>
                            <td className="border p-2 whitespace-nowrap">{row.guardianName}</td>
                            <td className="border p-2 whitespace-nowrap">{row.guardianPhone}</td>
                            <td className="border p-2 whitespace-nowrap">{row.dob}</td>
                            <td className="border p-2 whitespace-nowrap capitalize">{row.status}</td>
                          </>
                        )}
                        <td className="border p-2 space-y-1">
                          <button onClick={() => updateStatus(row.id, "approved")} className="bg-green-600 px-2 py-1 rounded mr-1">Approve</button>
                          <button onClick={() => { const notes = prompt("Enter reason for decline:"); if (notes) updateStatus(row.id, "declined", notes); }} className="bg-red-600 px-2 py-1 rounded mr-1">Decline</button>
                          <button onClick={() => updateStatus(row.id, "banned")} className="bg-black px-2 py-1 rounded mr-1">Ban</button>
                          <button onClick={() => updateStatus(row.id, "left")} className="bg-yellow-600 px-2 py-1 rounded">Mark as Left</button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
