import { useState, useEffect } from "react";

const Card = ({ className = "", children }) => (
  <div className={`bg-gray-900/70 backdrop-blur-md border border-purple-700 rounded-2xl shadow-lg p-4 ${className}`}>{children}</div>
);

const CardContent = ({ className = "", children }) => (
  <div className={`space-y-6 ${className}`}>{children}</div>
);

export default function CreatorFieldsCard({ formData, handleChange }) {
  const [showOtherPlatform, setShowOtherPlatform] = useState(false);
  const [showOtherLanguage, setShowOtherLanguage] = useState(false);
  const [showOtherSoftware, setShowOtherSoftware] = useState(false);

  const platforms = ["YouTube", "Twitch", "Facebook", "Instagram", "Kick", "Other"];
  const languages = ["Afrikaans", "English", "Other"];
  const softwareOptions = ["OBS", "Streamlabs", "Facebook Live Studio", "YouTube Live Studio", "TikTok Live Studio", "Other"];

  useEffect(() => {
    setShowOtherPlatform(Array.isArray(formData.platforms) && formData.platforms.includes("Other"));
    setShowOtherLanguage(Array.isArray(formData.languages) && formData.languages.includes("Other"));
    setShowOtherSoftware(Array.isArray(formData.software) && formData.software.includes("Other"));
  }, [formData.platforms, formData.languages, formData.software]);

  const handleMultiCheckboxChange = (e, field) => {
    const value = e.target.value;
    const checked = e.target.checked;
    const prevValues = Array.isArray(formData[field]) ? formData[field] : [];
    const updatedValues = new Set(prevValues);
    checked ? updatedValues.add(value) : updatedValues.delete(value);
    handleChange({ target: { name: field, value: Array.from(updatedValues) } });
  };

  return (
    <Card>
      <CardContent>
        <h2 className="text-2xl font-bold text-pink-400 border-b border-purple-600 pb-2">Content Creator & Streamer Info</h2>

        <section>
          <label className="block text-purple-300 font-semibold mb-1">Creator Name</label>
          <input name="creatorName" value={formData.creatorName} onChange={handleChange} className="recruitment-input" placeholder="Creator Name" />
          <label className="block text-purple-300 font-semibold mt-4 mb-1">Timezone</label>
          <input name="timezone" value={formData.timezone} onChange={handleChange} className="recruitment-input" placeholder="Timezone" />
        </section>

        <section>
          <label className="block text-purple-300 font-semibold mb-2">Streaming Platforms</label>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
            {platforms.map((platform) => (
              <label key={platform} className="flex items-center gap-2">
                <input type="checkbox" value={platform} checked={formData.platforms?.includes(platform)} onChange={(e) => handleMultiCheckboxChange(e, "platforms")} className="accent-purple-500" />
                <span>{platform}</span>
              </label>
            ))}
          </div>
          {showOtherPlatform && (
            <input name="otherPlatform" value={formData.otherPlatform || ""} onChange={handleChange} className="recruitment-input mt-2" placeholder="Other Platform" />
          )}
        </section>

        {(formData.platforms || []).map((platform) => (
          <section key={platform}>
            <label className="block text-sm text-purple-300 font-semibold">{platform} URL</label>
            <input name={`url_${platform}`} value={formData[`url_${platform}`] || ""} onChange={handleChange} className="recruitment-input" placeholder={`${platform} Channel URL`} />
            <label className="block text-sm text-purple-300 font-semibold mt-2">{platform} Followers</label>
            <input name={`followers_${platform}`} value={formData[`followers_${platform}`] || ""} onChange={handleChange} className="recruitment-input" placeholder={`${platform} Followers`} />
          </section>
        ))}

        <section>
          <label className="block text-purple-300 font-semibold mb-1">Streaming Schedule</label>
          <input name="schedule" value={formData.schedule || ""} onChange={handleChange} className="recruitment-input" placeholder="e.g., Mon 8PM, Fri 7PM" />
        </section>

        <section>
          <label className="block text-purple-300 font-semibold mb-1">Games Played</label>
          <input name="games" value={formData.games || ""} onChange={handleChange} className="recruitment-input" placeholder="List of Games" />
        </section>

        <section>
          <label className="block text-purple-300 font-semibold mb-2">Languages</label>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
            {languages.map((lang) => (
              <label key={lang} className="flex items-center gap-2">
                <input type="checkbox" value={lang} checked={formData.languages?.includes(lang)} onChange={(e) => handleMultiCheckboxChange(e, "languages")} className="accent-purple-500" />
                <span>{lang}</span>
              </label>
            ))}
          </div>
          {showOtherLanguage && (
            <input name="otherLanguage" value={formData.otherLanguage || ""} onChange={handleChange} className="recruitment-input mt-2" placeholder="Other Language" />
          )}
        </section>

        <section>
          <label className="block text-purple-300 font-semibold mb-1">Internet Speed</label>
          <input name="internet" value={formData.internet || ""} onChange={handleChange} className="recruitment-input" placeholder="e.g., 100Mbps" />

          <label className="block text-purple-300 font-semibold mt-4 mb-2">Streaming Software</label>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
            {softwareOptions.map((software) => (
              <label key={software} className="flex items-center gap-2">
                <input type="checkbox" value={software} checked={formData.software?.includes(software)} onChange={(e) => handleMultiCheckboxChange(e, "software")} className="accent-purple-500" />
                <span>{software}</span>
              </label>
            ))}
          </div>
          {showOtherSoftware && (
            <input name="otherSoftware" value={formData.otherSoftware || ""} onChange={handleChange} className="recruitment-input mt-2" placeholder="Other Software" />
          )}
        </section>

        <section>
          <label className="block text-purple-300 font-semibold mb-1">Microphone / Camera Setup</label>
          <textarea name="equipment" value={formData.equipment || ""} onChange={handleChange} className="recruitment-input" placeholder="Describe your setup" />
          <label className="flex items-center gap-2 mt-2">
            <input type="checkbox" name="camera" checked={formData.camera || false} onChange={handleChange} className="accent-purple-500" />
            <span>Use camera while streaming?</span>
          </label>
        </section>

        <section>
          <label className="block text-purple-300 font-semibold mb-1">Start Year of Content Creation</label>
          <input type="number" name="yearsCreating" value={formData.yearsCreating || ""} onChange={handleChange} className="recruitment-input" />
        </section>

        <section>
          <label className="flex items-center gap-2">
            <input type="checkbox" name="sponsors" checked={formData.sponsors || false} onChange={handleChange} className="accent-purple-500" />
            <span>Are you sponsored?</span>
          </label>
          {formData.sponsors && (
            <input name="sponsorList" value={formData.sponsorList || ""} onChange={handleChange} className="recruitment-input mt-2" placeholder="List of Sponsors" />
          )}
        </section>

        <section>
          <label className="block text-purple-300 font-semibold mb-1">Open to Collabs / Events?</label>
          <select name="collabs" value={formData.collabs || "no"} onChange={handleChange} className="recruitment-input">
            <option value="yes">Yes</option>
            <option value="no">No</option>
          </select>

          <textarea name="creatorGoals" value={formData.creatorGoals || ""} onChange={handleChange} className="recruitment-input mt-4" placeholder="Goals as a Creator" />
          <textarea name="creatorNotes" value={formData.creatorNotes || ""} onChange={handleChange} className="recruitment-input" placeholder="Additional Notes" />
        </section>
      </CardContent>
    </Card>
  );
}
