// Enhanced Admin Dashboard with Filters, Notes, and Modal Editing
import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";
import { CSVLink } from "react-csv";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable"; 

export default function AdminDashboard() {
  const [view, setView] = useState("overview");
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [modalData, setModalData] = useState(null);
  const [counts, setCounts] = useState({ total: 0, approved: 0, declined: 0, full: 0 });

  const fetchData = async (table) => {
    setLoading(true);
    const { data, error } = await supabase.from(table).select("*").order("created_at", { ascending: true });
    if (error) console.error(`Error fetching ${table}:`, error);
    setData(data || []);
    setFilteredData(data || []);
    setLoading(false);
  };

  const fetchOverviewStats = async () => {
    const { count: pending } = await supabase.from("applicants").select("id", { count: "exact" }).eq("status", "pending");
    const { count: approved } = await supabase.from("applicants").select("id", { count: "exact" }).eq("status", "approved");
    const { count: declined } = await supabase.from("applicants").select("id", { count: "exact" }).eq("status", "declined");
    const full = (pending || 0) + (approved || 0) + (declined || 0);
    setCounts({ total: pending || 0, approved: approved || 0, declined: declined || 0, full });
  };

  const updateStatus = async (id, newStatus, notes = "") => {
    const { error } = await supabase.from(view).update({ status: newStatus, decline_notes: notes }).eq("id", id);
    if (error) alert("Status update failed: " + error.message);
    else fetchData(view);
  };

  const updateField = async (id, field, value) => {
    if (field === "fullName" || field === "dob") return;
    const { error } = await supabase.from(view).update({ [field]: value }).eq("id", id);
    if (error) alert(`Update failed: ${error.message}`);
    else fetchData(view);
  };

const exportPDF = () => {
  const doc = new jsPDF();

  if (!data.length) {
    alert("No data to export");
    return;
  }

  // Define columns you want in the PDF
  const keys = [
    "id",
    "created_at",
    "fullName",
    "email",
    "phone",
    "division",
    "competitive",
    "status",
    "isCreator",
    "yearsCreating",
    "creatorGoals",
    "photo_url",
  ];

  const formatHeader = (key) =>
    key
      .replace(/_/g, " ")
      .replace(/([a-z])([A-Z])/g, "$1 $2")
      .replace(/\b\w/g, (l) => l.toUpperCase());

  const headers = keys.map(formatHeader);

  const rows = data.map((row) =>
    keys.map((key) => {
      let val = row[key];
      if (Array.isArray(val)) return val.join(", ");
      if (typeof val === "boolean") return val ? "Yes" : "No";
      if (typeof val === "string" && val.length > 50) return val.slice(0, 47) + "...";
      return val ?? "";
    })
  );

  doc.text(`${view.toUpperCase()} Export`, 14, 16);
  autoTable(doc, {
    head: [headers],
    body: rows,
    startY: 20,
    styles: { fontSize: 8, cellPadding: 2 },
    headStyles: { fillColor: [124, 58, 237], textColor: 255 },
    margin: { left: 10, right: 10 },
    tableWidth: 'auto',
  });

  doc.save(`${view}_export.pdf`);
};

  useEffect(() => {
    if (view === "overview") fetchOverviewStats();
    else fetchData(view);
  }, [view]);

  useEffect(() => {
    if (search.trim() === "") setFilteredData(data);
    else setFilteredData(data.filter(row => Object.values(row).join(" ").toLowerCase().includes(search.toLowerCase())));
  }, [search, data]);

  const columns = view === "applicants"
    ? ["Full Name", "Email", "Phone", "Division", "Competitive", "Status", "Photo", "Notes", "Created", "Actions"]
    : ["Full Name", "Guardian Name", "Phone", "DOB", "Status", "Notes", "Created", "Actions"];

  return (
    <div className="min-h-screen p-6 text-white bg-cover bg-center" style={{ backgroundImage: "url('https://eetgxwgbysyezclsecwd.supabase.co/storage/v1/object/public/public-assets//wallpaperflare.com_wallpaper%20(57).jpg')" }}>
      <div className="bg-gray-900 bg-opacity-90 p-6 rounded-xl shadow-lg max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold mb-6 text-purple-400 text-center">Admin Dashboard</h1>

        <div className="flex justify-center gap-4 mb-4">
          <button onClick={() => setView("overview")} className={`px-4 py-2 rounded ${view === "overview" ? "bg-purple-600" : "bg-gray-700"}`}>Overview</button>
          <button onClick={() => setView("applicants")} className={`px-4 py-2 rounded ${view === "applicants" ? "bg-purple-600" : "bg-gray-700"}`}>Applicants</button>
          <button onClick={() => setView("minor_applicants")} className={`px-4 py-2 rounded ${view === "minor_applicants" ? "bg-purple-600" : "bg-gray-700"}`}>Minors</button>
        </div>

        {view !== "overview" && (
          <div className="flex justify-between mb-3">
            <input type="text" value={search} onChange={(e) => setSearch(e.target.value)} placeholder="Search..." className="p-2 rounded bg-gray-800 border border-gray-600 w-1/3" />
            <div className="flex gap-2">
              <CSVLink data={data} filename={`${view}.csv`} className="bg-blue-500 px-4 py-2 rounded text-white">Export CSV</CSVLink>
              <button onClick={exportPDF} className="bg-red-500 px-4 py-2 rounded text-white">Export PDF</button>
            </div>
          </div>
        )}

        {view === "overview" ? (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
            {Object.entries(counts).map(([key, val]) => (
              <div key={key} className="bg-gray-700 p-4 rounded-lg">
                <p className="text-sm font-semibold capitalize">{key}</p>
                <p className="text-lg font-bold">{val}</p>
              </div>
            ))}
          </div>
        ) : loading ? (
          <p className="text-center text-lg">Loading...</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full text-sm border border-gray-600 mt-2">
              <thead>
                <tr className="bg-purple-800 text-white">
                  {columns.map(col => (
                    <th key={col} className="border border-gray-600 p-2">{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {filteredData.map(row => (
                  <tr key={row.id} className="hover:bg-gray-800">
                    <td className="border p-2">{row.fullName}</td>
                    {view === "applicants" ? (
                      <>
                        <td className="border p-2">{row.email}</td>
                        <td className="border p-2">{row.phone}</td>
                        <td className="border p-2">{row.division}</td>
                        <td className="border p-2">{row.competitive}</td>
                        <td className="border p-2 capitalize">{row.status}</td>
                        <td className="border p-2 text-center">{row.photo_url ? <a href={row.photo_url} target="_blank" rel="noopener noreferrer" className="text-blue-400 underline">View</a> : "N/A"}</td>
                        <td className="border p-2">{row.decline_notes || "-"}</td>
                        <td className="border p-2">{new Date(row.created_at).toLocaleDateString()}</td>
                      </>
                    ) : (
                      <>
                        <td className="border p-2">{row.guardianName}</td>
                        <td className="border p-2">{row.guardianPhone}</td>
                        <td className="border p-2">{row.dob}</td>
                        <td className="border p-2 capitalize">{row.status}</td>
                        <td className="border p-2">{row.decline_notes || "-"}</td>
                        <td className="border p-2">{new Date(row.created_at).toLocaleDateString()}</td>
                      </>
                    )}
                    <td className="border p-2 space-y-1">
                      <button onClick={() => updateStatus(row.id, "approved")} className="bg-green-600 px-2 py-1 rounded mr-1">Approve</button>
                      <button onClick={() => { const notes = prompt("Enter decline reason:"); if (notes) updateStatus(row.id, "declined", notes); }} className="bg-red-600 px-2 py-1 rounded mr-1">Decline</button>
                      <button onClick={() => updateStatus(row.id, "banned")} className="bg-black px-2 py-1 rounded mr-1">Ban</button>
                      <button onClick={() => setModalData(row)} className="bg-blue-500 px-2 py-1 rounded">Edit</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

       {modalData && (
  <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
    <div className="bg-white text-black p-6 rounded-xl shadow-xl max-w-lg w-full max-h-[90vh] overflow-y-auto">
      <h2 className="text-lg font-semibold mb-4">Edit Applicant</h2>

      {Object.entries(modalData).map(([key, value]) => {
        if (["id", "fullName", "dob"].includes(key)) return null;

        const handleFieldChange = (e) => {
          let newValue = e.target.value;

          // Convert CSV text to array for multi-fields
          if (["platforms", "languages", "software"].includes(key)) {
            newValue = newValue.split(",").map((s) => s.trim()).filter(Boolean);
          }

          // Convert select to boolean
          if (["camera", "sponsors", "ndaAgreement", "isCreator"].includes(key)) {
            newValue = e.target.value === "true";
          }

          setModalData({ ...modalData, [key]: newValue });
        };

        return (
          <div key={key} className="mb-3">
            <label className="block text-sm font-semibold mb-1 capitalize">{key}</label>

            {/* Boolean dropdown */}
            {["camera", "sponsors", "isCreator", "ndaAgreement"].includes(key) ? (
              <select
                className="w-full p-2 border rounded"
                value={String(value)}
                onChange={handleFieldChange}
              >
                <option value="true">Yes</option>
                <option value="false">No</option>
              </select>

            ) : key === "collabs" ? (
              <select
                className="w-full p-2 border rounded"
                value={value}
                onChange={handleFieldChange}
              >
                <option value="yes">Yes</option>
                <option value="no">No</option>
              </select>

            ) : ["platforms", "languages", "software"].includes(key) ? (
              <textarea
                rows={2}
                className="w-full p-2 border rounded"
                value={Array.isArray(value) ? value.join(", ") : value}
                onChange={handleFieldChange}
              />
            ) : (
              <input
                className="w-full p-2 border rounded"
                type="text"
                value={value || ""}
                onChange={handleFieldChange}
              />
            )}
          </div>
        );
      })}

      <div className="flex justify-end gap-2 mt-4">
        <button onClick={() => setModalData(null)} className="bg-gray-500 px-4 py-2 rounded text-white">Cancel</button>
        <button onClick={() => {
          Object.entries(modalData).forEach(([k, v]) => {
            if (!["id", "fullName", "dob"].includes(k)) updateField(modalData.id, k, v);
          });
          setModalData(null);
        }} className="bg-purple-600 px-4 py-2 rounded text-white">Save</button>
      </div>
    </div>
  </div>
)}
      </div>
    </div>
  );
}
