import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";

export default function AdminUserManager() {
  const [users, setUsers] = useState([]);
  const [role, setRole] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [search, setSearch] = useState("");

  const [confirmAction, setConfirmAction] = useState(null); // { id, newRole }

  // Auth Check
  useEffect(() => {
    const checkAuth = async () => {
      const {
        data: { user },
      } = await supabase.auth.getUser();

      if (!user) return setError("Not logged in.");

      const { data, error } = await supabase
        .from("users")
        .select("role")
        .eq("id", user.id)
        .single();

      if (error || data?.role !== "superadmin") {
        return setError("Access denied. Superadmin only.");
      }

      setRole(data.role);
    };

    checkAuth();
  }, []);

  // Fetch Users
  useEffect(() => {
    if (role === "superadmin") {
      const fetchUsers = async () => {
        const { data, error } = await supabase
          .from("users")
          .select("id, email, role, created_at")
          .order("created_at", { ascending: true });

        if (error) {
          setError("Failed to fetch users");
        } else {
          setUsers(data);
        }

        setLoading(false);
      };

      fetchUsers();
    }
  }, [role]);

  const handleConfirm = async () => {
    const { id, newRole } = confirmAction;
    const { error } = await supabase.from("users").update({ role: newRole }).eq("id", id);

    if (!error) {
      setUsers((prev) =>
        prev.map((u) => (u.id === id ? { ...u, role: newRole } : u))
      );
    } else {
      alert("Failed to update role: " + error.message);
    }

    setConfirmAction(null);
  };

  const filteredUsers = users.filter((user) =>
    user.email.toLowerCase().includes(search.toLowerCase()) ||
    user.role.toLowerCase().includes(search.toLowerCase())
  );

  if (error) return <div className="p-10 text-center text-red-500 font-bold">{error}</div>;
  if (loading) return <div className="p-10 text-center">Loading users...</div>;

  return (
    <div className="max-w-5xl mx-auto p-6 relative">
      <h1 className="text-2xl font-bold mb-4 text-center">Superadmin Panel</h1>

      <input
        type="text"
        placeholder="Search by email or role"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-4 w-full px-4 py-2 border rounded-md"
      />

      <table className="w-full border-collapse rounded shadow overflow-hidden">
        <thead className="bg-gray-100 text-left">
          <tr>
            <th className="p-3">Email</th>
            <th className="p-3">Role</th>
            <th className="p-3">Joined</th>
            <th className="p-3 text-center">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user) => (
            <tr key={user.id} className="border-t">
              <td className="p-3">{user.email}</td>
              <td className="p-3 capitalize">{user.role}</td>
              <td className="p-3 text-sm text-gray-500">
                {new Date(user.created_at).toLocaleDateString()}
              </td>
              <td className="p-3 text-center space-x-2">
                {user.role === "user" && (
                  <>
                    <button
                      onClick={() =>
                        setConfirmAction({ id: user.id, newRole: "admin" })
                      }
                      className="bg-purple-600 hover:bg-purple-700 text-white px-3 py-1 rounded text-sm"
                    >
                      Promote to Admin
                    </button>
                    <button
                      onClick={() =>
                        setConfirmAction({ id: user.id, newRole: "superadmin" })
                      }
                      className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm"
                    >
                      Promote to Superadmin
                    </button>
                  </>
                )}
                {user.role === "admin" && (
                  <button
                    onClick={() =>
                      setConfirmAction({ id: user.id, newRole: "user" })
                    }
                    className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded text-sm"
                  >
                    Revoke Admin
                  </button>
                )}
                {user.role === "superadmin" && (
                  <span className="text-xs text-gray-400 italic">Superadmin</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Confirmation Modal */}
      {confirmAction && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl shadow-xl max-w-sm text-center space-y-4">
            <h2 className="text-lg font-semibold text-gray-800">
              Confirm Role Change
            </h2>
            <p className="text-gray-600 text-sm">
              Are you sure you want to{" "}
              <span className="font-semibold text-blue-700">
                set this user as <span className="capitalize">{confirmAction.newRole}</span>
              </span>
              ?
            </p>
            <div className="flex justify-center gap-4 pt-2">
              <button
                onClick={handleConfirm}
                className="bg-green-600 text-white px-4 py-1 rounded hover:bg-green-700"
              >
                Confirm
              </button>
              <button
                onClick={() => setConfirmAction(null)}
                className="bg-gray-300 px-4 py-1 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
