import { useState, useEffect } from "react";

const Card = ({ className = "", children }) => (
  <div className={`bg-gray-800/60 backdrop-blur border border-gray-700 rounded-2xl shadow ${className}`}>{children}</div>
);

const CardContent = ({ className = "", children }) => (
  <div className={`p-4 space-y-4 ${className}`}>{children}</div>
);

export default function CreatorFieldsCard({ formData, handleChange }) {
  const [showOtherPlatform, setShowOtherPlatform] = useState(false);
  const [showOtherLanguage, setShowOtherLanguage] = useState(false);
  const [showOtherSoftware, setShowOtherSoftware] = useState(false);

  const platforms = ["YouTube", "Twitch", "Facebook", "Instagram", "Kick", "Other"];
  const languages = ["Afrikaans", "English", "Other"];
  const softwareOptions = ["OBS", "Streamlabs", "Facebook Live Studio", "YouTube Live Studio", "TikTok Live Studio", "Other"];

  useEffect(() => {
    setShowOtherPlatform(Array.isArray(formData.platforms) && formData.platforms.includes("Other"));
    setShowOtherLanguage(Array.isArray(formData.languages) && formData.languages.includes("Other"));
    setShowOtherSoftware(Array.isArray(formData.software) && formData.software.includes("Other"));
  }, [formData.platforms, formData.languages, formData.software]);

  const handleMultiCheckboxChange = (e, field) => {
    const value = e.target.value;
    const checked = e.target.checked;
    const prevValues = Array.isArray(formData[field]) ? formData[field] : [];
    const updatedValues = new Set(prevValues);
    checked ? updatedValues.add(value) : updatedValues.delete(value);
    handleChange({ target: { name: field, value: Array.from(updatedValues) } });
  };

  return (
    <Card className="bg-gray-800 border border-gray-700">
      <CardContent className="space-y-4 p-4">
        <h2 className="text-xl font-semibold text-blue-400 mb-2">Content Creator & Streamer Info</h2>

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Basic Info</h3>
          <input name="creatorName" placeholder="Creator Name" value={formData.creatorName} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
          <input name="timezone" placeholder="Timezone" value={formData.timezone} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
        </div>

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Streaming Platforms</h3>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
            {platforms.map((platform) => (
              <label key={platform} className="flex items-center space-x-2">
                <input type="checkbox" value={platform} checked={Array.isArray(formData.platforms) && formData.platforms.includes(platform)} onChange={(e) => handleMultiCheckboxChange(e, "platforms")} className="text-purple-500" />
                <span>{platform}</span>
              </label>
            ))}
          </div>
          {showOtherPlatform && (
            <input name="otherPlatform" placeholder="Specify Other Platform" value={formData.otherPlatform || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm mt-2" />
          )}
        </div>

        {(formData.platforms || []).map((platform) => (
          <div key={platform}>
            <input name={`url_${platform}`} placeholder={`${platform} Channel URL`} value={formData[`url_${platform}`] || ""} onChange={handleChange} required className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
            <input name={`followers_${platform}`} placeholder={`${platform} Follower Count`} value={formData[`followers_${platform}`] || ""} onChange={handleChange} required className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
          </div>
        ))}

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Schedule</h3>
          <input name="schedule" placeholder="e.g., Monday 8PM, Friday 7PM (SAST)" value={formData.schedule || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
        </div>

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Games Played</h3>
          <input name="games" placeholder="List of games" value={formData.games || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
        </div>

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Languages</h3>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
            {languages.map((lang) => (
              <label key={lang} className="flex items-center space-x-2">
                <input type="checkbox" value={lang} checked={Array.isArray(formData.languages) && formData.languages.includes(lang)} onChange={(e) => handleMultiCheckboxChange(e, "languages")} className="text-purple-500" />
                <span>{lang}</span>
              </label>
            ))}
          </div>
          {showOtherLanguage && (
            <input name="otherLanguage" placeholder="Specify Other Language" value={formData.otherLanguage || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm mt-2" />
          )}
        </div>

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Technical Info</h3>
          <input name="internet" placeholder="Internet Speed" value={formData.internet || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />

          <h3 className="text-lg font-semibold text-purple-300 mt-4">Software Used</h3>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
            {softwareOptions.map((software) => (
              <label key={software} className="flex items-center space-x-2">
                <input type="checkbox" value={software} checked={Array.isArray(formData.software) && formData.software.includes(software)} onChange={(e) => handleMultiCheckboxChange(e, "software")} className="text-purple-500" />
                <span>{software}</span>
              </label>
            ))}
          </div>
          {showOtherSoftware && (
            <input name="otherSoftware" placeholder="Specify Other Software" value={formData.otherSoftware || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm mt-2" />
          )}

          <textarea name="equipment" placeholder="Microphone / Camera Setup" value={formData.equipment || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm mt-4" />

          <label className="flex items-center space-x-2 mt-2">
            <input type="checkbox" name="camera" checked={formData.camera || false} onChange={handleChange} className="h-4 w-4 text-purple-600 focus:ring-purple-500 border-gray-300 rounded" />
            <span className="text-sm text-gray-200">Use camera while streaming?</span>
          </label>
        </div>

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Experience</h3>
          <input name="yearsCreating" type="number" placeholder="Start Year of Creating Content" value={formData.yearsCreating || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
        </div>

        <div>
          <label className="flex items-center space-x-2">
            <input type="checkbox" name="sponsors" checked={formData.sponsors || false} onChange={handleChange} className="h-4 w-4 text-purple-600 focus:ring-purple-500 border-gray-300 rounded" />
            <span className="text-sm text-gray-200">Are you sponsored?</span>
          </label>
          {formData.sponsors && (
            <input name="sponsorList" placeholder="List Sponsors" value={formData.sponsorList || ""} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
          )}
        </div>

        <div>
          <h3 className="text-lg font-semibold text-purple-300">Additional</h3>
          <label className="block text-sm text-gray-200 mb-1">Open to collabs/events?</label>
          <select name="collabs" value={formData.collabs || "no"} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 text-sm">
            <option value="yes">Yes</option>
            <option value="no">No</option>
          </select>

          <textarea name="creatorGoals" placeholder="Goals as a Content Creator" value={formData.creatorGoals} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm mt-4" />
          <textarea name="creatorNotes" placeholder="Additional Notes" value={formData.creatorNotes} onChange={handleChange} className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 placeholder-gray-400 text-sm" />
        </div>
      </CardContent>
    </Card>
  );
}
