import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";
import { useNavigate } from "react-router-dom";

export default function UserManagementPage() {
  const [users, setUsers] = useState([]);
  const [currentUserRole, setCurrentUserRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        navigate("/"); // redirect to login/entry
        return;
      }

      const { data: userData, error: userError } = await supabase
        .from("users")
        .select("role")
        .eq("id", user.id)
        .single();

      if (userError || !userData || userData.role !== "superadmin") {
        navigate("/"); // Only superadmins can view this page
        return;
      }

      setCurrentUserRole(userData.role);

      const { data, error } = await supabase.from("users").select("id, email, role");
      if (!error) setUsers(data);
      setLoading(false);
    };

    fetchData();
  }, [navigate]);

  const promoteToAdmin = async (id) => {
    const { error } = await supabase
      .from("users")
      .update({ role: "admin" })
      .eq("id", id);

    if (!error) {
      setUsers((prev) =>
        prev.map((u) => (u.id === id ? { ...u, role: "admin" } : u))
      );
    }
  };

  if (loading) return <div className="text-center mt-10">Loading users...</div>;

  return (
    <div className="p-6 max-w-4xl mx-auto text-white">
      <h1 className="text-3xl font-bold mb-4">User Management</h1>

      <table className="w-full table-auto bg-white/10 rounded-lg shadow-xl">
        <thead>
          <tr className="bg-purple-800 text-white">
            <th className="px-4 py-2 text-left">Email</th>
            <th className="px-4 py-2 text-left">Role</th>
            <th className="px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id} className="border-t border-purple-600">
              <td className="px-4 py-2">{user.email}</td>
              <td className="px-4 py-2 capitalize">{user.role}</td>
              <td className="px-4 py-2 text-center">
                {user.role === "user" ? (
                  <button
                    onClick={() => promoteToAdmin(user.id)}
                    className="bg-green-600 hover:bg-green-700 text-white px-4 py-1 rounded"
                  >
                    Promote to Admin
                  </button>
                ) : (
                  <span className="text-gray-400 italic">No action</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
